import error.calculate_error as errimport models.take_dataframe as tdimport pandas as pdgercek_df = td.take_real()baslangic_ayi = 1  # Örnek olarak Ocak ayıbitis_ayi = 8  # Örnek olarak Ağustos ayıerrors = []file_path = 'pred_excels/'last_path_pred = '_pred.xlsx'names = ['cr','des','ses','sma','wh','tsb']forecasts = []for name in names:    forecasts.append(pd.read_excel(file_path+name+last_path_pred))        for forecast in forecasts:    hata_metrikleri = err.error_metrics(forecast, gercek_df, baslangic_ayi, bitis_ayi)    errors.append(hata_metrikleri)    errors[0].to_excel('error_excels/cr_error.xlsx', index=False)errors[1].to_excel('error_excels/des_error.xlsx', index=False)errors[2].to_excel('error_excels/ses_error.xlsx', index=False)errors[3].to_excel('error_excels/sma_error.xlsx', index=False)errors[4].to_excel('error_excels/wh_error.xlsx', index=False)errors[5].to_excel('error_excels/tsb_error.xlsx', index=False)    